import React, { useState } from 'react'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faSquarePlus, faTrash, faPenToSquare} from '@fortawesome/free-solid-svg-icons';
import "./style.css";

function Todo(){
    const [Input,setInput]=useState(null);
    const [InputList,setInputList]=useState([]);
    const [Toggle,setToggle]=useState(true);
    // function randomcolor(){
    //     let chars="0123456789ABCDE";
    //     let hash="#";
    //     for(let i=0;i<6;i++){
    //         hash+=chars[Math.floor(Math.random()*16)];
    //     }
    //     console.log(hash);
    //     return hash;    
    // }

    function deletedata(id){
        let afterDelete=InputList.filter((data)=>{
            return data.id != id
        })
        setInputList(afterDelete);

    }



    function InputData()
    {
        if(Input)
        {
            let newData={
                id: new Date().getTime().toString(),
                listData:Input
            }
        setInputList((data)=>{
            return [...data,newData]
        })
    }
        setInput("");

    }
    function editData(){

    }
    console.log(InputList);
    return(

        <div className='container'>
            <h1>ToDo List</h1>
            <div>
                <input type='text' placeholder='Please Enter here ✍️ ' value={Input} onChange={(e)=> {setInput(e.target.value)}} />
                {Toggle ? <FontAwesomeIcon icon={faSquarePlus} beat size="2xl" style={{color: "#53f80d",marginLeft:"10px"}} onClick={InputData}/>:
                <FontAwesomeIcon icon={faPenToSquare} beat size="2xl" style={{color: "#c66210",}} onClick={InputData}  /> 
                 }
            </div>
            <div className='listValue'>
                {InputList.length>0 ? <h3 style={{textAlign:"left"}}>Task Count:{InputList.length} </h3>: " "}
                {
                    InputList.map((value)=>{

                        return(
                            <>

                            <div key={value.id}  > 
                                    {value.listData}
                            </div>
                            <FontAwesomeIcon icon={faTrash} size="2xl" style={{color: "#f70814",}} onClick={()=>{deletedata(value.id);}}/>
                            <FontAwesomeIcon icon={faPenToSquare}  size="2xl"  style={{color: "#c66210", marginLeft:'20px'}} onClick={()=>{editData(value.id);}} />
                            </>
                        )

                    })
                }

            </div>
        </div>

    )
}

export default Todo;
