import React, { useState } from 'react'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faSquarePlus, faTrash, faPenToSquare} from '@fortawesome/free-solid-svg-icons';
import "./style.css";

document.getElementsByTagName(body).style.background="red"

function Todo(){
    const [Input,setInput]=useState(null);
    const [InputList,setInputList]=useState([]);



    function deletedata(id){
        let afterDelete=InputList.filter((data)=>{
            return data.id != id
        })
        setInputList(afterDelete);

    }



    function InputData()
    {
        if(Input)
        {
            let newData={
                id: new Date().getTime().toString(),
                listData:Input
            }
        setInputList((data)=>{
            return [...data,newData]
        })
    }
        setInput("");

    }
    console.log(InputList);
    return(
        <div className='container'>
            <h1>ToDo List</h1>
            <div>
                <input type='text' placeholder='Please Enter here ✍️ ' value={Input} onChange={(e)=> {setInput(e.target.value)}} />
                <FontAwesomeIcon icon={faSquarePlus} beat size="2xl" style={{color: "#53f80d",marginLeft:"10px"}} onClick={InputData}/>
            </div>
            <div className='listValue'>
                {InputList.length>0 ? <h3 style={{textAlign:"left"}}>Task Count:{InputList.length} </h3>: " "}
                {
                    InputList.map((value)=>{

                        return(
                            <>

                            <div key={value.id}  > 
                                    {value.listData}
                            </div>
                            <FontAwesomeIcon icon={faTrash} size="2xl" style={{color: "#f70814",}} onClick={()=>{deletedata(value.id);}}/>
                            <FontAwesomeIcon icon={faPenToSquare}  size="2xl"  style={{color: "#c66210", marginLeft:'20px'}} />
                            </>
                        )

                    })
                }

            </div>
        </div>

    )
}

export default Todo;
